CustomField.formula:
IF(
    AND(
        NOT(ISBLANK(Day_started_time__c)),
        NOT(
            ISBLANK(
                IF(Is_System_Ended__c, Last_Visit_Date_and_Time__c, Day_ended_time__c)
            )
        )
    ),
    ROUND(
        (IF(Is_System_Ended__c, Last_Visit_Date_and_Time__c, Day_ended_time__c) - Day_started_time__c) * 24,
        2
    ),
    NULL
)
